import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { fetchOffers } from '../../actions/getOffers';




import './offer.css';


class Offer extends Component {

    state = {
        isFetching: false,
        offer: null,
        message: null,
    }

    static propTypes = {
        dispatch: PropTypes.func,
        isFetching: PropTypes.bool,
        offer: PropTypes.object,
        message: PropTypes.object,
        clickHandler: PropTypes.func,
    }


    


    async componentDidMount() {
        const { dispatch } = this.props;
        let offers = await this.props.offer;
        await this.setState({
            offer: offers,
        })
        dispatch(fetchOffers());

    }



    handleButtonClick = (e) => {
        const {result} = this.props.offer;
        const index = e.currentTarget.getAttribute('id');

        this.props.clickHandler(result[index]);
      }

    render() {
        const { isFetching, offer } = this.props;
        if (isFetching || !offer) {
            return (
                <div>
                    Sæki tilboð...
                </div>
            );
        }
        let { result } = offer;
        
        
        if(!result){
            return(
                <div>
                    <h2>Síða finnst ekki, vinsamlegast reyndu aftur</h2>
                </div>
            )
        }



    return (
      <div className = "content">
    
        <ul className = "offer-list">

            {result.map(offers =>
                <button key={offers.id} id={result.indexOf(offers)} onClick={this.handleButtonClick} style = {{marginTop: '1em'}}><li key={offers.id}>
                    
                        <div className = "offer-item">
                            <div className = "name">
                                <h2> {offers.name} </h2>
                            </div>
                            <div className = "offerImage">
                                <img src={require('../../Images/Menu-items/' + offers.name + '.png')} alt = "Mynd finst ekki" />

                            </div>
                            <div className = "description">
                                {offers.description}
                            </div>
                            <div className = "price">
                                <span> Verð: </span>
                                {offers.price}

                            </div>
                        </div>
                    
                </li></button>
                
                

                
            )}
        </ul>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
    return {
      isFetching: state.getOffers.isFetching,
      offer: state.getOffers.offer,
      message: state.getOffers.message,
    };
  }
  
export default connect(mapStateToProps)(Offer);
